import React, { Component, useState,useEffect } from 'react';
 import './App.css';
// import {Route, Link} from 'react-router-dom';
// import NavBar from './components/navbar.jsx';
// import Home from './components/pages/home.jsx';
// import Search from './components/pages/search.jsx';
import {Movie} from './components/movie.js';
import {Home} from './components/pages/home.js';

function App() {
  const [movies, setMovie] = useState([]);
  const [search, setSearch] = useState("")
  const handleChange = e => { setSearch(e.target.value) }
  const filterMovies = movies.filter(movie => movie.name.toLowerCase().includes(search.toLowerCase()))
  useEffect(() => {
      fetch('/home').then(response => {
          if (response.ok) {
              return response.json()
          }
      }).then(data => {
          console.log(data);
          setMovie(data)
      })
  }, [])
  return (
      <>
          <div className='App'>
              <div className='movie-search'>
                  <h1 className='movie-text'>Search a Movie</h1>
                  <form>
                      <input type='text' placeholder='Search' className='movie-input' onChange={handleChange}/>
                  </form>
              </div>
              {filterMovies.map(movie => {
                  return <Movie key={movie.id}
                  name = {movie.name} />
              })}
          </div>
      </>
  );
}

