import {Home} from './compnents/'

function App() {
  const [movies, setMovie] = useState([]);
  const [search,setSearch] = useState("")
  const filterMovies = movies.filter(movie => movie.name.toLowerCase().includes(search.toLowerCase()))
  const handleChange = e => {setSearch(e.target.value)}
  return (
    <div class='App'>
      <div class='movie-search'>
        <h1 class='movie-text'>Search a Movie</h1>
        <form>
          <input type='text'  placeholder='Search' class='movie-input' onChange={handleChange}/>
        </form>
      </div>
      <Movie />
    </div>
  );
}

